function adaptiveImage(){document.querySelectorAll(".ibg").forEach(e=>{"IMG"!==e.firstElementChild.tagName||e.firstElementChild.classList.contains("no-linear")?e.style.backgroundImage=`url( ${e.firstElementChild.getAttribute("src")} ) `:e.style.backgroundImage=`linear-gradient(180deg, #251447 0%, rgba(43, 29, 112, 0.4) 60%, rgba(37, 20, 71, 0.7) 72.4%, #251447 100%),\n                 url( ${e.firstElementChild.getAttribute("src")} ) `})}adaptiveImage();